mapper.select:
SELECT i.inbound_id, s_from.storage_name AS departStorageName,
GROUP_CONCAT(CONCAT(p.product_name, ': ', ii.quantity) SEPARATOR ', ') AS products, i.status, i.request_date, s_to.storage_name AS arriveStorageName, u_to.user_id FROM Inbound i JOIN User u_to ON i.user_id = u_to.user_id LEFT JOIN Storage s_from ON u.storage_id = s_from.storage_id LEFT JOIN Storage s_to ON i.depart_storage_id = s_to.storage_id LEFT JOIN User u_from ON s_from.storage_id = u_from.storage_id JOIN Inbound_Item ii ON ii.inbound_id = i.inbound_id JOIN Product p ON ii.product_id = p.product_id WHERE 1=1 AND status IN('반려', '미승인', '승인')
AND i.status = #{status}
AND DATE(i.request_date) BETWEEN #{startDate} AND #{endDate}
AND p.product_name LIKE CONCAT('%', #{item}, '%')
AND s_to.storage_name LIKE CONCAT('%', #{departStorageName}, '%')
AND s_from.storage_name LIKE CONCAT('%', #{inboundStorageName}, '%')
GROUP BY i.inbound_id, departStorageName, arriveStorageName, u_to.user_id, i.status, i.request_date
ORDER BY i.inbound_id DESC LIMIT #{skip}, #{size}

SELECT i.inbound_id, s_from.storage_name AS arriveStorageName,
GROUP_CONCAT(CONCAT(p.product_name, ': ', ii.quantity) SEPARATOR ', ') AS products, i.status, i.inbound_complete_date, s_to.storage_name AS departStorageName FROM Inbound i JOIN User u ON i.user_id = u.user_id JOIN Storage s_from ON u.storage_id = s_from.storage_id LEFT JOIN Storage s_to ON i.depart_storage_id = s_to.storage_id JOIN Item ii ON ii.inbound_id = i.inbound_id JOIN Product p ON ii.product_id = p.product_id WHERE 1=1 AND status IN('승인','출고완료','입고완료')
AND i.status = #{status}
AND i.inbound_complete_date BETWEEN #{startDate} AND #{endDate}
AND p.product_name LIKE CONCAT('%', #{item}, '%')
AND s_to.storage_name LIKE CONCAT('%', #{departStorageName}, '%')
AND s_from.storage_name LIKE CONCAT('%', #{inboundStorageName}, '%')
GROUP BY i.inbound_id, departStorageName, arriveStorageName, i.status, i.request_date
ORDER BY i.inbound_id DESC LIMIT #{skip}, #{size}

