mapper.insert:
INSERT INTO outbound (user_id, request_date

    , arrive_storage_id


    , outbound_mart

)
VALUES (#{userId}, now()

    , (SELECT storage_id FROM storage WHERE storage_name = #{arriveName})


    , #{outboundMart}

)

    SELECT LAST_INSERT_ID()

mapper.select:
SELECT o.outbound_id, s_from.storage_name AS departStorageName,
CASE WHEN o.arrive_storage_id IS NULL THEN '귤로벌마트' ELSE s_to.storage_name END AS arriveName,
u_from.user_id AS outbound_user_id,
GROUP_CONCAT(CONCAT(p.product_name, ': ', oi.quantity) SEPARATOR ', ') AS products,
o.status, o.request_date FROM Outbound o JOIN User u_from ON o.user_id = u_from.user_id JOIN Storage s_from ON u_from.storage_id = s_from.storage_id LEFT JOIN Storage s_to ON o.arrive_storage_id = s_to.storage_id LEFT JOIN User u_to ON s_to.storage_id = u_to.storage_id JOIN Outbound_Item oi ON oi.outbound_id = o.outbound_id JOIN Product p ON oi.product_id = p.product_id WHERE 1=1 AND o.status IN ('반려', '미승인', '승인')
AND o.status = #{status}
AND o.request_date BETWEEN #{startDate} AND #{endDate}
AND p.product_name LIKE CONCAT('%', #{item}, '%')
AND s_to.storage_name LIKE CONCAT('%', #{arriveName}, '%')
AND s_from.storage_name LIKE CONCAT('%', #{outboundStorageName}, '%')
AND o.outbound_mart = #{outboundMart}
GROUP BY o.outbound_id, departStorageName, arriveName, u_from.user_id, u_to.user_id, o.status, o.request_date
ORDER BY o.outbound_id DESC LIMIT #{skip}, #{size}

SELECT o.outbound_id,
CASE WHEN o.arrive_storage_id IS NULL THEN '귤로벌마트' ELSE s_to.storage_name END AS arriveName,
GROUP_CONCAT(CONCAT(p.product_name, ': ', oi.quantity) SEPARATOR ', ') AS products, o.status, o.inbound_complete_date, s_from.storage_name AS departStorageName FROM Outbound o JOIN User u ON o.user_id = u.user_id JOIN Storage s_from ON u.storage_id = s_from.storage_id LEFT JOIN Storage s_to ON o.arrive_storage_id = s_to.storage_id JOIN Outbound_Item oi ON oi.outbound_id = o.outbound_id JOIN Product p ON oi.product_id = p.product_id WHERE 1=1 AND status IN('승인', '출고완료', '입고완료')
AND o.status = #{status}
AND o.inbound_complete_date BETWEEN #{startDate} AND #{endDate}
AND p.product_name LIKE CONCAT('%', #{item}, '%')
AND s_to.storage_name LIKE CONCAT('%', #{arriveName}, '%')
AND s_from.storage_name LIKE CONCAT('%', #{outboundStorageName}, '%')
AND o.outbound_mart = #{outboundMart}
GROUP BY o.outbound_id, s_from.storage_name, arriveName, o.status
ORDER BY o.outbound_id DESC LIMIT #{skip}, #{size}

mapper.update:
UPDATE Outbound
SET status = '승인'
WHERE outbound_id IN (

    #{id}

)
AND status = '미승인'

UPDATE Outbound
SET status = '반려'
WHERE outbound_id IN (

    #{id}

)
AND status = '미승인'
